package flowcontrol;

public class FlowControl1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean x=false;
		boolean y=true;
		if(x){
			System.out.println("if block");
		}
		else{
			System.out.println("else block");
		}
		
		if(y==true){
			System.out.println("if block");
		}
		else{
			System.out.println("else block");
		}
	}

}
